import { Routes, Route, NavLink, Navigate } from 'react-router-dom';
import './RootNavigator.scss';
import { Home } from '../Home/Home';
import { Library } from '../Library/Library';
import { Liturgy } from '../Liturgy/Liturgy';
import { Ectenias } from '../Ectenias/Ectenias';
import { Item } from '../Item/Item';
import { useEffect, useState } from 'react';

const ecteniasList = [
  { path: 'kijowska', title: 'Ektenia wielka Kijowska' },
  { path: 'sofijska', title: 'Ektenia wielka Sofijska' },
  { path: 'archangielski', title: 'Ektenia wielka Archangielski' },
  { path: 'moskiewska', title: 'Ektenia wielka Moskiewska' },
  { path: 'stecenko-1910', title: 'Ektenia wielka Stecenko z liturgii 1910 roku', uprPath: 'stecenko-1910-uproszcz', uprTitle: 'Uproszczony' },
  { path: 'stecenko-1907', title: 'Ektenia wielka Stecenko z liturgii 1907 roku' },
].sort((a, b) => a.title.localeCompare(b.title));

const partOfService = {
  'ectenias': ecteniasList,
}

export const RootNavigator = () => {
  const [element, setElement] = useState({});
  const currentPath = window.location.hash
    .split('#/').join('')
    .split('/');

  const service = currentPath[1];
  const currentService = currentPath[2];

  useEffect(() => {
    if (currentPath[3]) {
      const index = partOfService[currentService].findIndex(arr => arr.path === currentPath[3]);
      setElement(partOfService[currentService][index]);
    }
  }, [currentPath, currentService])

  const getClasses = (status) => {
    return status.isActive ? 'list__link is-active' : 'list__link';
  };

  console.log(service, element.path, currentService);

  return (
    <>
      <nav className="navigation">
        <div className="list">
          <NavLink
            to="/"
            className={getClasses}
          >
            Home
          </NavLink>
          <NavLink
            to="/library"
            className={getClasses}
          >
            Library
          </NavLink>
        </div>
      </nav>

      <div className="page">
        <Routes>
          <Route path="/" element={<Home />} />

          <Route path="/library">
            <Route index element={<Library />} />

            <Route path="liturgy">
              <Route index element={<Liturgy />} />

{/* Тут треба зробити список з можливих наповнень */}
              <Route path={`${currentService}`}>
                <Route index element={<Ectenias list={partOfService[currentService]} />} />
                {/* <Route path={element.path} element={<Item toShow={element} />} /> */}
                {partOfService[currentService].map}
              </Route>
            </Route>
          </Route>
          <Route path="/home" element={<Navigate to="/" replace />} />
        </Routes>
      </div>
    </>
  )
}
